import React, { Component } from "react";
import Messenger from "../Messenger";
var request = require("request");

// use only request to
//http://172.22.27.9:3000/api/v1/getMessages?username=2
const MessageDemo = () => {
  request.post(
    {
      url: "http://192.168.43.201:3000/api/v1/sendMessage",
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token")
      },
      form: {
        username: "Krishna",
        message: "hello"
      }
    },
    (err, res, body) => {
      if (err) throw err;
    }
  );
  return <Messenger />;
};

export default MessageDemo;
