import React from "react";
import loginImg from "../../logo.svg";
import axios from "axios";
var rp = require("request-promise");
export class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  handleRegister(event) {
    AccessToken("hello", "password");
    console.log("hello");
    event.preventDefault();
  }
  render() {
    return (
      <div className="base-container" ref={this.props.containerRef}>
        <div className="header">Register</div>
        <div className="content">
          <div className="image">
            <img src={loginImg} />
          </div>
          <div className="form">
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input type="text" name="username" placeholder="username" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="text" name="password" placeholder="passowrd" />
            </div>
          </div>
        </div>

        <div className="footer" onClick={this.handleRegister.bind(this)}>
          <button type="button" className="btn">
            Register
          </button>
        </div>
      </div>
    );
  }
}
function AccessToken(username, password) {
  // "node-server-clc-herokuapp.com/api/v1/create";
  // let config = {
  //   headers: {
  //     "Content-Type": "application/x-www-form-urlencoded"
  //   }
  // };

  // axios
  //   .post("http://192.168.137.1:3000/api/v1/create", {
  //     body: {
  //       username: "hello",
  //       password: "password"
  //     },
  //     config
  //   })
  //   .then(response => {
  //     handleResponse(response);
  //   })
  //   .catch(err => {
  //     console.log(err);
  //   });
  var options = {
    method: "POST",
    uri: "http://192.168.137.1:3000/api/v1/create",
    form: {
      username: "hello",
      password: "1234"
    }
  };
  rp(options)
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err);
    });
}
function handleResponse(response) {
  if (response === "Username Exists") {
    alert("Username Exists");
  }
}

export default Register;
