import React from "react";
import loginImg from "../../logo.svg";
var rp = require("request-promise");
export class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "",
      passWord: ""
    };
  }
  handleLoginButton(event) {
    var token = AccessToken(this.state.userName, this.state.passWord);
    console.log(this.state.userName + token);
    event.preventDefault();
  }
  handleUserName = event => {
    this.setState({ userName: event.target.value });
  };
  handlePassWord = event => {
    this.setState({ passWord: event.target.value });
  };
  render() {
    return (
      <div className="base-container" ref={this.props.containerRef}>
        <div className="header">Login</div>
        <div className="content">
          <div className="image">
            <img src={loginImg} />
          </div>
          <div className="form">
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                name="username"
                placeholder="username"
                onChange={this.handleUserName.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                name="password"
                placeholder="Password"
                onChange={this.handlePassWord.bind(this)}
              />
            </div>
          </div>
        </div>
        <div className="footer" onClick={this.handleLoginButton.bind(this)}>
          <button type="button" className="btn">
            Login
          </button>
        </div>
      </div>
    );
  }
}
function AccessToken(username, password) {
  var options = {
    method: "POST",
    uri: "http://192.168.137.1:3000/api/v1/login",
    form: {
      username: "hello",
      password: "1234"
    }
  };
  rp(options)
    .then(response => {
      return response;
    })
    .catch(err => {
      console.log(err);
    });
}
function handleResponse(response, username) {
  if (response === "Username Exists") {
    alert("Username  already exists Exists");
  } else if (response === "Internal db error") {
    alert("Something went wrong try again later");
  } else {
    alert("Successfully insertd");
  }
}
