import React from "react";
import { Login } from "./components/login/index";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: true
    };
  }
  render() {
    const { isLogginActive } = this.state;
    return (
      <div className="App">
        <div className="login">
          <div className="container">
            {isLogginActive && (
              <Login containerRef={ref => (this.current = ref)} />
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
