import React, { Component } from "react";
import axios from "axios";

var rp = require("request-promise");
var request = require("request");

export default function MessengeDemo(props) {
  var options = {
    method: "GET",
    uri: "http://172.22.27.9:3000/api/v1/getMessages?username=Krishna",
    Headers: {
      Authorization:
        "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiZ2FuZXNoIiwicGFzc3dvcmQiOiJicm9pdHNtZSJ9LCJpYXQiOjE1NzcxODgxMDF9.zzFv1qO4G75rjLUk31rQBinL52BwlBqy30YJccgZlR4"
    }
  };

  rp(options)
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err);
    });
