  // };

  // axios.get(url, data, config).then(response => {
  //   console.log(Response);
  // });
  request.get(
    {
      url: "http://172.22.27.9:3000/api/v1/getMessages?username=ganesh",
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiS3Jpc2huYSIsInBhc3N3b3JkIjoiMTIzNCJ9LCJpYXQiOjE1NzcxOTIyNjh9.27AKrKgbcPCCKBOYLEw7xpppTFHz2671pNCkz4Cjc9w"
      }
    },
    (err, res, body) => {
      if (err) throw err;
      console.log(res);
      console.log(body);
    }
  );

  return <div>Message</div>;
}
