import React from "react";
import "./App.scss";
import { Main } from "./components/login/index";

import MessageDemo from "./components/chat/MessageDemo/messagedemo";
import { BrowserRouter, Route } from "react-router-dom";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userToken: "",
      rememberMe: false
    };
  }
  componentDidMount() {
    this.setState({ rememberMe: localStorage.getItem("rememberMe") });
    this.setState({ userToken: localStorage.getItem("token") });
    console.log(localStorage.getItem("rememberMe"));
  }
  render() {
    // if (this.state.rememberMe) {
    //   return <MessageDemo />;
    // } else {
    //   return <Main />;
    // }

    return (
      <BrowserRouter>
        <div>
          <Route path="/" exact Component={Main} />
          <Route path="/chat" exact Component={MessageDemo} />
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
