import React, { Component } from "react";
import Messenger from "../Messenger/messenger";
var request = require("request");

// use only request to
class MessageDemo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null
    };
  }

  render() {
    request.get(
      {
        url:
          "https://node-server-t.herokuapp.com/api/v1/getMessages?username=ganesh",
        headers: {
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiS3Jpc2huYSIsInBhc3N3b3JkIjoiMTIzNCJ9LCJpYXQiOjE1NzcxOTIyNjh9.27AKrKgbcPCCKBOYLEw7xpppTFHz2671pNCkz4Cjc9w"
        }
      },
      (err, res, body) => {
        if (err) throw err;
        this.setState({ data: body });
      }
    );
    return <Messenger data={this.state.data} />;
  }
}

export default MessageDemo;
