import React, { Component } from "react";

var rp = require("request-promise");

export default function MessengeDemo(props) {
  var options = {
    method: "POST",
    uri: "http://node-server-clc.herokuapp.com/api/v1/sendMessage",
    headers: {
      Authorization: localStorage.getItem("token")
    },
    form: {
      username: "hello",
      message: "passWord"
    }
  };

  rp(options)
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err);
    });
  return <div>Message</div>;
}
