import React, { useState, useEffect } from "react";

import "./App.scss";
import MessageDemo from "./components/chat/MessageDemo/messagedemo";

import { Main } from "./components/chat/MessageDemo/messagedemo";
export default function App(props) {
  const [userToken, setUserToken] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  useEffect(() => {
    updateUI();
  }, rememberMe);

  const updateUI = () => {
    setUserToken([userToken, localStorage.getItem("token")]);
    setRememberMe([rememberMe, localStorage.getItem("rememberMe")]);
  };
  if (rememberMe) return <MessageDemo />;
  else return <Main />;
}
