import React, { Component } from "react";
import Messenger from "../Messenger/messenger";
var request = require("request");

// use only request to
//http://172.22.27.9:3000/api/v1/getMessages?username=2
const MessageDemo = () => {
  request.post(
    {
      url: "http://172.22.27.9:3000/api/v1/sendMessage",
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiMSIsInBhc3N3b3JkIjoiYnJvaXRzbWUifSwiaWF0IjoxNTc3Mjk5ODcxfQ.Y-v-2xIlDI_zS7U6FEmJQrK2pqsl-T6QHndts-KDMiY"
      },
      form: {
        username: "2",
        message: "hello"
      }
    },
    (err, res, body) => {
      if (err) throw err;
    }
  );
  return <Messenger />;
};

export default MessageDemo;
